import React, { useState } from 'react';
import './App.css';

function App() {
  // Состояние для активной вкладки
  const [activeTab, setActiveTab] = useState(0);

  // Функция для изменения активной вкладки
  const handleTabClick = (index) => {
    setActiveTab(index);
  };

  return (
    <div className="App">
      <div className="background"></div>
      <div className="content-box">
        {/* Кнопки для переключения вкладок */}
        <div className="buttons-container">
          {[1, 2, 3, 4].map((number) => (
            <div key={number} className="button" onClick={() => handleTabClick(number)}>
              <div className="button-background"></div>
              <div className="button-text">
                Лабораторная <br /> работа
              </div>
              <div className="button-number">{number}</div>
            </div>
          ))}
        </div>

        {/* Вкладки */}
        <div className="tabs-container">
          {/* Первая вкладка */}
          {activeTab === 1 && (
            <div className="tab">
              <div className="tab-background"></div>
            </div>
          )}

          {/* Вторая вкладка */}
          {activeTab === 2 && (
            <div className="tab">
              <div className="tab-background"></div>
            </div>
          )}

          {/* Третья вкладка */}
          {activeTab === 3 && (
            <div className="tab">
              <div className="tab-background"></div>
            </div>
          )}

          {/* Четвертая вкладка */}
          {activeTab === 4 && (
            <div className="tab">
              <div className="tab-background"></div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
