import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState(0);

  const [tabs, setTabs] = useState([
    { title: 'Главная', content: 'Контент главной страницы' },
  ]);

  const addTab = () => {
    const newTabIndex = tabs.length + 1;
    setTabs([
      ...tabs,
      { title: `Лабораторная ${newTabIndex}`, content: '' }, // Пустой контент для новой вкладки
    ]);
    setActiveTab(tabs.length); // Переключаем на новую вкладку
  };

  return (
    <div className="App">
      <div className="background"></div>

      {/* Вкладки находятся в верхней части экрана */}
      <div className="tabs-container">
        {tabs.map((tab, index) => (
          <div
            key={index}
            className={`tab ${activeTab === index ? 'active' : ''}`}
            onClick={() => setActiveTab(index)}
          >
            {tab.title}
          </div>
        ))}
        <div className="add-tab-button" onClick={addTab}>
          + Добавить вкладку
        </div>
      </div>

      <div className="content-box">
        {/* Контент активной вкладки */}
        <div className="tab-content">
          {activeTab === 0 ? (
            // Контент для главной вкладки
            <div className="main-tab-content">
              <div className="header-text">
                Лабораторный практикум<br />по предмету “Управление данными”
              </div>
              <div className="buttons-container">
                {[1, 2, 3, 4].map((number) => (
                  <div key={number} className="button">
                    <div className="button-background"></div>
                    <div className="button-text">
                      Лабораторная <br /> работа
                    </div>
                    <div className="button-number">{number}</div>
                  </div>
                ))}
              </div>
            </div>
          ) : tabs[activeTab]?.content ? (
            // Контент для остальных вкладок, если есть контент
            <div>
              <h2>{tabs[activeTab]?.title}</h2>
              <p>{tabs[activeTab]?.content}</p>
            </div>
          ) : (
            // Если контент пустой, показываем только название вкладки
            <h2>{tabs[activeTab]?.title}</h2>
          )}
        </div>
      </div>
    </div>
  );
}

export default App;
