import React, { useState } from 'react';
import './App.css';

function App() {
  const [activeTab, setActiveTab] = useState(0); // Индекс активной вкладки
  const [tabs, setTabs] = useState([
    { title: 'Главная', content: 'Контент главной страницы' },
  ]);

  // Функция для добавления вкладки или переключения на существующую
  const addTab = (title) => {
    const existingTabIndex = tabs.findIndex((tab) => tab.title === title);

    if (existingTabIndex !== -1) {
      // Если вкладка уже существует, переключаемся на неё
      setActiveTab(existingTabIndex);
    } else {
      // Если вкладка не существует, создаем новую
      setTabs([
        ...tabs,
        { title, content: '' }, // Пустой контент для новых вкладок
      ]);
      setActiveTab(tabs.length); // Переключаем на новую вкладку
    }
  };

  const closeOtherTabs = (index) => {
    // Если возвращаемся на Главную, все остальные вкладки закрываются
    if (index === 0) {
      setTabs([{ title: 'Главная', content: 'Контент главной страницы' }]); // Оставляем только главную вкладку
    }
    setActiveTab(index); // Переключаемся на выбранную вкладку
  };

  return (
    <div className="App">
      <div className="background"></div>

      {/* Вкладки находятся в верхней части экрана */}
      <div className="tabs-container">
        {tabs.map((tab, index) => (
          <div
            key={index}
            className={`tab ${activeTab === index ? 'active' : ''}`}
            onClick={() => closeOtherTabs(index)}
          >
            {tab.title}
          </div>
        ))}
      </div>

      {/* Рендерим контент только для активной вкладки */}
      {activeTab === 0 && (
        <div className="content-box">
          <div className="tab-content">
            {/* Контент главной вкладки */}
            <div className="main-tab-content">
              <div className="header-text">
                Лабораторный практикум<br />по предмету “Управление данными”
              </div>
              <div className="buttons-container">
                {[1, 2, 3, 4].map((number) => (
                  <div key={number} className="button" onClick={() => addTab(`Лабораторная работа №${number}`)}>
                    <div className="button-background"></div>
                    <div className="button-text">
                      Лабораторная <br /> работа
                    </div>
                    <div className="button-number">{number}</div>
                  </div>
                ))}
              </div>
            </div>
          </div>
        </div>
      )}

      {activeTab !== 0 && (
        <div className="content-box">
          <div className="tab-content">
            <h2>{tabs[activeTab]?.title}</h2>
            <p>{tabs[activeTab]?.content}</p>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
