import React, { useState } from 'react';
import './Tabs.css'; // Файл с CSS-стилями для вкладок

function Tabs() {
  // Состояние для хранения текущей активной вкладки
  const [activeTab, setActiveTab] = useState('home');

  // Функция для смены вкладки
  const handleTabChange = (tab) => {
    setActiveTab(tab);
  };

  return (
    <div className="tabs-container">
      {/* Панель с кнопками для переключения вкладок */}
      <div className="tabs-buttons">
        <button onClick={() => handleTabChange('home')}>Главная</button>
        <button onClick={() => handleTabChange('about')}>О нас</button>
        <button onClick={() => handleTabChange('contact')}>Контакты</button>
      </div>

      {/* Контент для активной вкладки */}
      <div className="tabs-content">
        {activeTab === 'home' && <div>Это главная вкладка.</div>}
        {activeTab === 'about' && <div>Это вкладка "О нас".</div>}
        {activeTab === 'contact' && <div>Это вкладка "Контакты".</div>}
      </div>
    </div>
  );
}

export default Tabs;
